import{_ as r,o as t,c as i,ae as a}from"./chunks/framework.9XX48Qcy.js";const b=JSON.parse('{"title":"Detaljeret Kronologi (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Detaljeret Kronologi (1993-2025)"},"headers":[],"relativePath":"da/history.md","filePath":"i18n/da/history.md"}'),s={name:"da/history.md"};function o(n,e,l,d,k,g){return t(),i("div",null,[...e[0]||(e[0]=[a('<h1 id="detaljeret-kronologi-1993-2025" tabindex="-1">Detaljeret Kronologi (1993-2025) <a class="header-anchor" href="#detaljeret-kronologi-1993-2025" aria-label="Permalink to &quot;Detaljeret Kronologi (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"><h3 id="_1993-2001-—-statsborgerskabsfælden" tabindex="-1">1993-2001 — Statsborgerskabsfælden <a class="header-anchor" href="#_1993-2001-—-statsborgerskabsfælden" aria-label="Permalink to &quot;1993-2001 — Statsborgerskabsfælden&quot;">​</a></h3><p>Født i Donbass — et land med arbejde og store udfordringer. Min barndom var præget af en juridisk anomali. Med to pas (Ukraine og Rusland) på grund af USSR&#39;s kollaps, blev jeg gidsel for et system, der brugte denne kollision som et våben mod min suverænitet.</p></div><div class="history-block"><h3 id="_2014-—-krig-i-donbass" tabindex="-1">2014 — Krig i Donbass <a class="header-anchor" href="#_2014-—-krig-i-donbass" aria-label="Permalink to &quot;2014 — Krig i Donbass&quot;">​</a></h3><p>Krigen tog mit hjem, min familie og min følelse af stabilitet. Efter 2014 blev min ejendom ulovligt beslaglagt. Dette var begyndelsen på min vej som et menneske, der må forsvare sin ret til at eksistere.</p></div><div class="history-block"><h3 id="_2020-2022-—-menneskerettighedsaktivisme" tabindex="-1">2020-2022 — Menneskerettighedsaktivisme <a class="header-anchor" href="#_2020-2022-—-menneskerettighedsaktivisme" aria-label="Permalink to &quot;2020-2022 — Menneskerettighedsaktivisme&quot;">​</a></h3><p>Aktiv deltagelse i bevægelsen for legalisering af medicinsk cannabis i Ukraina sammen med &#39;Rastamany Ukraine&#39; bloggere. Kæmpede for folks ret til alternativ behandling og reduktion af lidelser.</p></div><div class="history-block"><h3 id="_2024-—-systemisk-undertrykkelse" tabindex="-1">2024 — Systemisk Undertrykkelse <a class="header-anchor" href="#_2024-—-systemisk-undertrykkelse" aria-label="Permalink to &quot;2024 — Systemisk Undertrykkelse&quot;">​</a></h3><p>I dag kæmper jeg mod bureaukratisk undertrykkelse i Tyskland. Frøvelse af rettigheder til lægehjælp og bevægelse er en moderne form for tortur rettet mot at knække viljen. Men jeg er et Levende Menneske, og jeg fortsætter med at skabe.</p></div><div class="history-block"><h3 id="_2025-—-økologiske-projekter" tabindex="-1">2025 — Økologiske Projekter <a class="header-anchor" href="#_2025-—-økologiske-projekter" aria-label="Permalink to &quot;2025 — Økologiske Projekter&quot;">​</a></h3><p>Udvikling af tre innovative løsninger for en bæredygtig fremtid:</p><p><strong>UCTS</strong> — system til omdannelse af giftigt rødslam fra aluminiumsindustrien til energi og byggematerialer. Et ingeniørsvar på den økologiske krise.</p><p><strong>Solar-H2</strong> — et hybrid maritimt fremdriftssystem, der kombinerer brintbrændselsceller og solenergi. En innovativ løsning til dekarbonisering af den maritime industri.</p><p><strong>Moss Tile</strong> — et naturligt bykølingssystem ved brug af levende mos. Integrerer teknologi i naturen for en balanceret og suveræn fremtid.</p></div></div>',2)])])}const h=r(s,[["render",o]]);export{b as __pageData,h as default};
