import{_ as r,o as t,c as n,ae as s}from"./chunks/framework.9XX48Qcy.js";const f=JSON.parse('{"title":"ERKL√ÜRING OM TILSTEDEV√ÜRELSE AF ET LEVENDE MENNESKE","description":"","frontmatter":{"layout":"doc","title":"ERKL√ÜRING OM TILSTEDEV√ÜRELSE AF ET LEVENDE MENNESKE"},"headers":[],"relativePath":"da/letters/mfa_iceland.md","filePath":"i18n/da/letters/mfa_iceland.md"}'),i={name:"da/letters/mfa_iceland.md"};function a(l,e,o,d,g,k){return t(),n("div",null,[...e[0]||(e[0]=[s('<h1 id="erkl√¶ring-om-tilstedev√¶relse-af-et-levende-menneske" tabindex="-1">ERKL√ÜRING OM TILSTEDEV√ÜRELSE AF ET LEVENDE MENNESKE <a class="header-anchor" href="#erkl√¶ring-om-tilstedev√¶relse-af-et-levende-menneske" aria-label="Permalink to &quot;ERKL√ÜRING OM TILSTEDEV√ÜRELSE AF ET LEVENDE MENNESKE&quot;">‚Äã</a></h1><div class="letter-print-container vp-doc"><div class="no-print" style="text-align:right;margin-bottom:1rem;"><a href="javascript:window.print()" class="link-btn">Udskriv Dokument üñ®Ô∏è</a></div><p><b>Til det Islandske Udenrigsministerium og internationale menneskerettighedsorganisationer.</b></p><div class="letter-body-content"><p>Jeg, Aleksandr Snigirev, erkl√¶rer ved denne offentlige erkl√¶ring tilstedev√¶relsen af et Levende Menneske, udstyret med √•nd, samvittighed og umistelige rettigheder modtaget ved f√∏dselsret p√• Jorden.</p><p>Denne erkl√¶ring er baseret p√• <strong>FN&#39;s Verdenserkl√¶ring om Menneskerettigheder</strong> (Artikler 1, 3, 6, 13, 14, 15) og h√∏jere Naturret.</p><h3 id="_1-juridisk-status" tabindex="-1">1. Juridisk Status <a class="header-anchor" href="#_1-juridisk-status" aria-label="Permalink to &quot;1. Juridisk Status&quot;">‚Äã</a></h3><p>Jeg erkl√¶rer, at jeg ikke er en &#39;fysisk person&#39;, &#39;√∏konomisk ressource&#39; eller nogen anden juridisk fiktion skabt af staten uden mit bevidste samtykke. Jeg handler som en fri mand, suver√¶n i mit liv. Min tilstedev√¶relse p√• denne Jord er selvforsynende, og mine rettigheder til liv, frihed og kreativitet er absolutte og umistelige.</p><h3 id="_2-kravets-essens-og-kr√¶nkelser-af-menneskerettigheder" tabindex="-1">2. Kravets Essens og Kr√¶nkelser af Menneskerettigheder <a class="header-anchor" href="#_2-kravets-essens-og-kr√¶nkelser-af-menneskerettigheder" aria-label="Permalink to &quot;2. Kravets Essens og Kr√¶nkelser af Menneskerettigheder&quot;">‚Äã</a></h3><p>I det forl√∏bne √•r har jeg v√¶ret under hidtil uset og systemisk bureaukratisk pres i Forbundsrepublikken Tyskland. Dette pres manifesterer sig som f√∏lger:</p><ul><li><strong>Fr√∏velse af Bev√¶gelsesfrihed</strong>: Min ret til frit valg af bop√¶l og bev√¶gelse er grundl√∏st begr√¶nset.</li><li><strong>Begr√¶nsning af Retten til Liv og Helbred</strong>: Jeg er blevet n√¶gtet adgang til n√∏dvendig l√¶gehj√¶lp, hvilket skaber en direkte trussel mod mit helbred.</li><li><strong>Psykologisk Pres</strong>: Systemet skaber &#39;digitale ghetto&#39;-forhold i et fors√∏g p√• at tvinge mig til at give afkald p√• mine overbevisninger og suver√¶ne status.</li></ul><p>Disse handlinger er direkte kr√¶nkelser af grundl√¶ggende menneskerettigheder og kan kvalificeres som tortur og umenneskelig behandling.</p><h3 id="_3-anmodning-om-beskyttelse-og-anerkendelse" tabindex="-1">3. Anmodning om Beskyttelse og Anerkendelse <a class="header-anchor" href="#_3-anmodning-om-beskyttelse-og-anerkendelse" aria-label="Permalink to &quot;3. Anmodning om Beskyttelse og Anerkendelse&quot;">‚Äã</a></h3><p>Jeg appellerer officielt til Regeringen og Folket i Republikken Island, som et land med dybe traditioner for respekt for menneskerettigheder og individuel suver√¶nitet:</p><ul><li><strong>Jeg anmoder om politisk asyl</strong> og international beskyttelse mod systemisk forf√∏lgelse.</li><li><strong>Jeg anmoder om anerkendelse af min status som et Levende Menneske</strong> og muligheden for at realisere mine ingeni√∏r- og kreative projekter til gavn for menneskeheden.</li></ul></div><div class="letter-signature"><p>Underskrivelsesdato: 5. februar 2024</p><p><b>Underskrift:</b> <span class="nowrap">Aleksandr Snigirev</span>, <br><span class="nowrap"><b>Levende Menneske</b></span></p></div></div>',2)])])}const p=r(i,[["render",a]]);export{f as __pageData,p as default};
