import{_ as i,o as n,c as r,ae as t}from"./chunks/framework.9XX48Qcy.js";const g=JSON.parse('{"title":"Detaillierte Chronologie (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Detaillierte Chronologie (1993-2025)"},"headers":[],"relativePath":"de/history.md","filePath":"i18n/de/history.md"}'),s={name:"de/history.md"};function a(o,e,d,l,u,h){return n(),r("div",null,[...e[0]||(e[0]=[t('<h1 id="detaillierte-chronologie-1993-2025" tabindex="-1">Detaillierte Chronologie (1993-2025) <a class="header-anchor" href="#detaillierte-chronologie-1993-2025" aria-label="Permalink to &quot;Detaillierte Chronologie (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"> ### 1993-2001 — Staatsbürgerschaftsfalle Geboren im Donbass – einem Land der Arbeit und großer Herausforderungen. Meine Kindheit war geprägt von einer rechtlichen Anomalie. Mit zwei Pässen (Ukraine und Russland) aufgrund des Zusammenbruchs der UdSSR wurde ich zur Geisel eines Systems, das diese Kollision als Waffe gegen meine Souveränität einsetzte. </div><div class="history-block"> ### 2014 — Krieg im Donbass Der Krieg nahm mir mein Zuhause, meine Familie und mein Gefühl der Stabilität. Nach 2014 wurde mein Eigentum illegal beschlagnahmt. Dies war der Beginn meines Weges als Mensch, der sein Recht auf Existenz verteidigen muss. </div><div class="history-block"> ### 2020-2022 — Menschenrechtsaktivismus Aktive Teilnahme an der Bewegung zur Legalisierung von medizinischem Cannabis in der Ukraine zusammen mit den &#39;Rastamany Ukraine&#39; Bloggern. Kampf für das Recht der Menschen auf alternative Behandlung und Linderung von Leiden. </div><div class="history-block"> ### 2024 — Systemische Unterdrückung Heute kämpfe ich gegen bürokratische Unterdrückung in Deutschland. Der Entzug von Rechten auf medizinische Versorgung und Bewegungsfreiheit ist eine moderne Form der Folter, die darauf abzielt, den Willen zu brechen. Aber ich bin ein Lebender Mensch, und ich erschaffe weiter. </div><div class="history-block"> ### 2025 — Ökologische Projekte Entwicklung von drei innovativen Lösungen für eine nachhaltige Zukunft: <p><strong>UCTS</strong> — System zur Umwandlung von giftigem Rotschlamm aus der Aluminiumindustrie in Energie und Baumaterialien. Eine Ingenieursantwort auf die ökologische Krise.</p><p><strong>Solar-H2</strong> — ein hybrides maritimes Antriebssystem, das Wasserstoffbrennstoffzellen und Solarenergie kombiniert. Eine innovative Lösung zur Dekarbonisierung der maritimen Industrie.</p><p><strong>Moss Tile</strong> — ein natürliches Stadtkühlsystem mit lebendem Moos. Integration von Technologie in die Natur für eine ausgewogene und souveräne Zukunft.</p></div></div>',2)])])}const m=i(s,[["render",a]]);export{g as __pageData,m as default};
