import{_ as t,o as a,c as n,ae as i}from"./chunks/framework.9XX48Qcy.js";const f=JSON.parse('{"title":"DECLARATION OF PRESENCE OF A LIVING MAN","description":"","frontmatter":{"layout":"doc","title":"DECLARATION OF PRESENCE OF A LIVING MAN"},"headers":[],"relativePath":"de/letters/mfa_iceland.md","filePath":"i18n/de/letters/mfa_iceland.md"}'),o={name:"de/letters/mfa_iceland.md"};function r(s,e,l,c,d,h){return a(),n("div",null,[...e[0]||(e[0]=[i('<h1 id="declaration-of-presence-of-a-living-man" tabindex="-1">DECLARATION OF PRESENCE OF A LIVING MAN <a class="header-anchor" href="#declaration-of-presence-of-a-living-man" aria-label="Permalink to &quot;DECLARATION OF PRESENCE OF A LIVING MAN&quot;">‚Äã</a></h1><div class="letter-print-container vp-doc"><div class="no-print" style="margin-bottom:2rem;"><a href="javascript:window.print()" class="link">Print Document üñ®Ô∏è</a></div><p><b>To the Ministry of Foreign Affairs of Iceland and international human rights organizations.</b></p><div class="letter-body-content"><p>I, Aleksandr Snigirev, by this public declaration declare the presence of a Living Man, endowed with spirit, conscience, and inalienable rights received by birthright on Earth.</p><p>This declaration is based on the <strong>UN Universal Declaration of Human Rights</strong> (Articles 1, 3, 6, 13, 14, 15) and higher Natural Law.</p><h3 id="_1-legal-status" tabindex="-1">1. Legal Status <a class="header-anchor" href="#_1-legal-status" aria-label="Permalink to &quot;1. Legal Status&quot;">‚Äã</a></h3><p>I declare that I am not a &quot;natural person&quot;, &quot;economic resource&quot;, or any other legal fiction created by the state without my conscious consent. I act as a free man, sovereign of my life. My presence on this Earth is self-sufficient, and my rights to life, liberty, and creativity are absolute and inalienable.</p><h3 id="_2-essence-of-claim-and-human-rights-violations" tabindex="-1">2. Essence of Claim and Human Rights Violations <a class="header-anchor" href="#_2-essence-of-claim-and-human-rights-violations" aria-label="Permalink to &quot;2. Essence of Claim and Human Rights Violations&quot;">‚Äã</a></h3><p>For the past year, I have been under unprecedented and systemic bureaucratic pressure within the Federal Republic of Germany. This pressure manifests as follows:</p><ul><li><strong>Deprivation of Freedom of Movement</strong>: My right to free choice of residence and movement is groundlessly restricted.</li><li><strong>Restriction of Right to Life and Health</strong>: I have been denied access to necessary medical assistance, creating a direct threat to my health.</li><li><strong>Psychological Pressure</strong>: The system creates &quot;digital ghetto&quot; conditions, attempting to force me to renounce my beliefs and sovereign status.</li></ul><p>These actions are direct violations of fundamental human rights and may qualify as torture and inhumane treatment.</p><h3 id="_3-request-for-protection-and-recognition" tabindex="-1">3. Request for Protection and Recognition <a class="header-anchor" href="#_3-request-for-protection-and-recognition" aria-label="Permalink to &quot;3. Request for Protection and Recognition&quot;">‚Äã</a></h3><p>I officially appeal to the Government and People of the Republic of Iceland, as a country with deep traditions of respect for human rights and individual sovereignty:</p><ul><li><strong>I request political asylum</strong> and international protection from systemic persecution.</li><li><strong>I request recognition of my status as a Living Man</strong> and the opportunity to realize my engineering and creative projects for the benefit of humanity.</li></ul><p>My developments (UCTS, Solar-H2) are aimed at solving global energy and environmental problems. I am ready to direct my intellectual potential to the development of Iceland and the World.</p><ul><li><a href="./../portfolio.html">View Portfolio of Engineering Solutions</a></li><li><a href="./../history.html">Read full detailed history of persecution</a></li></ul></div><div class="letter-signature"><p>Date of signing: February 5, 2024</p><p><b>Signature:</b> <span class="nowrap">Aleksandr Snigirev</span>, <br><span class="nowrap"><b>Living Man</b></span></p></div></div>',2)])])}const p=t(o,[["render",r]]);export{f as __pageData,p as default};
