import{_ as a,o as n,c as t,ae as e}from"./chunks/framework.9XX48Qcy.js";const d=JSON.parse('{"title":"Yksityiskohtainen Kronologia (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Yksityiskohtainen Kronologia (1993-2025)"},"headers":[],"relativePath":"fi/history.md","filePath":"i18n/fi/history.md"}'),s={name:"fi/history.md"};function o(l,i,r,k,u,m){return n(),t("div",null,[...i[0]||(i[0]=[e('<h1 id="yksityiskohtainen-kronologia-1993-2025" tabindex="-1">Yksityiskohtainen Kronologia (1993-2025) <a class="header-anchor" href="#yksityiskohtainen-kronologia-1993-2025" aria-label="Permalink to &quot;Yksityiskohtainen Kronologia (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"> ### 1993-2001 — Kansalaisuusansa Synnyin Donbassissa — työn ja suurten haasteiden maassa. Lapsuuttani leimasi juridinen epäkohta. Kahden passin (Ukraina ja Venäjä) vuoksi Neuvostoliiton romahduksen jälkeen jäin järjestelmän panttivangiksi, joka käytti tätä ristiriitaa aseena suvereniteettiani vastaan. </div><div class="history-block"> ### 2014 — Sota Donbassissa Sota vei kotini, perheeni ja vakauden tunteen. Vuoden 2014 jälkeen omaisuuteni takavarikoitiin laittomasti. Tämä oli alkua polulleni ihmisenä, jonka on puolustettava oikeuttaan olla olemassa. </div><div class="history-block"> ### 2020-2022 — Ihmisoikeusaktivismi Aktiivinen osallistuminen lääkekannabiksen laillistamisliikkeeseen Ukrainassa yhdessä &#39;Rastamany Ukraine&#39; -bloggaajien kanssa. Taistelu ihmisten oikeudesta vaihtoehtoisiin hoitoihin ja kärsimyksen vähentämiseen. </div><div class="history-block"> ### 2024 — Systeeminen Sorto Tänään taistelen byrokraattista sortoa vastaan Saksassa. Oikeuksien riistäminen lääkärihoitoon ja liikkumiseen on moderni kidutuksen muoto, jonka tavoitteena on murtaa tahto. Mutta olen Elävä Ihminen, ja jatkan luomista. </div><div class="history-block"> ### 2025 — Ekologiset Projektit Kolmen innovatiivisen ratkaisun kehittäminen kestävää tulevaisuutta varten: <p><strong>UCTS</strong> — järjestelmä alumiiniteollisuuden myrkyllisen punalietteen muuntamiseksi energiaksi ja rakennusmateriaaleiksi. Insinöörin vastaus ekologiseen kriisiin.</p><p><strong>Solar-H2</strong> — hybridi merenkulun propulsiojärjestelmä, joka yhdistää vetypolttokennot ja aurinkoenergian. Innovatiivinen ratkaisu meriteollisuuden hiilidioksidipäästöjen vähentämiseksi.</p><p><strong>Moss Tile</strong> — luonnollinen kaupunkiviilennysjärjestelmä elävää sammalta käyttäen. Teknologian integrointi luontoon tasapainoista ja suvereenia tulevaisuutta varten.</p></div></div>',2)])])}const h=a(s,[["render",o]]);export{d as __pageData,h as default};
