import{_ as a,o as s,c as e,ae as t}from"./chunks/framework.9XX48Qcy.js";const d=JSON.parse('{"title":"Yksityiskohtainen Kronologia (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Yksityiskohtainen Kronologia (1993-2025)"},"headers":[],"relativePath":"fi/history.md","filePath":"i18n/fi/history.md"}'),n={name:"fi/history.md"};function o(l,i,r,k,u,h){return s(),e("div",null,[...i[0]||(i[0]=[t('<h1 id="yksityiskohtainen-kronologia-1993-2025" tabindex="-1">Yksityiskohtainen Kronologia (1993-2025) <a class="header-anchor" href="#yksityiskohtainen-kronologia-1993-2025" aria-label="Permalink to &quot;Yksityiskohtainen Kronologia (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"><h3 id="_1993-2001-—-kansalaisuusansa" tabindex="-1">1993-2001 — Kansalaisuusansa <a class="header-anchor" href="#_1993-2001-—-kansalaisuusansa" aria-label="Permalink to &quot;1993-2001 — Kansalaisuusansa&quot;">​</a></h3><p>Synnyin Donbassissa — työn ja suurten haasteiden maassa. Lapsuuttani leimasi juridinen epäkohta. Kahden passin (Ukraina ja Venäjä) vuoksi Neuvostoliiton romahduksen jälkeen jäin järjestelmän panttivangiksi, joka käytti tätä ristiriitaa aseena suvereniteettiani vastaan.</p></div><div class="history-block"><h3 id="_2014-—-sota-donbassissa" tabindex="-1">2014 — Sota Donbassissa <a class="header-anchor" href="#_2014-—-sota-donbassissa" aria-label="Permalink to &quot;2014 — Sota Donbassissa&quot;">​</a></h3><p>Sota vei kotini, perheeni ja vakauden tunteen. Vuoden 2014 jälkeen omaisuuteni takavarikoitiin laittomasti. Tämä oli alkua polulleni ihmisenä, jonka on puolustettava oikeuttaan olla olemassa.</p></div><div class="history-block"><h3 id="_2020-2022-—-ihmisoikeusaktivismi" tabindex="-1">2020-2022 — Ihmisoikeusaktivismi <a class="header-anchor" href="#_2020-2022-—-ihmisoikeusaktivismi" aria-label="Permalink to &quot;2020-2022 — Ihmisoikeusaktivismi&quot;">​</a></h3><p>Aktiivinen osallistuminen lääkekannabiksen laillistamisliikkeeseen Ukrainassa yhdessä &#39;Rastamany Ukraine&#39; -bloggaajien kanssa. Taistelu ihmisten oikeudesta vaihtoehtoisiin hoitoihin ja kärsimyksen vähentämiseen.</p></div><div class="history-block"><h3 id="_2024-—-systeeminen-sorto" tabindex="-1">2024 — Systeeminen Sorto <a class="header-anchor" href="#_2024-—-systeeminen-sorto" aria-label="Permalink to &quot;2024 — Systeeminen Sorto&quot;">​</a></h3><p>Tänään taistelen byrokraattista sortoa vastaan Saksassa. Oikeuksien riistäminen lääkärihoitoon ja liikkumiseen on moderni kidutuksen muoto, jonka tavoitteena on murtaa tahto. Mutta olen Elävä Ihminen, ja jatkan luomista.</p></div><div class="history-block"><h3 id="_2025-—-ekologiset-projektit" tabindex="-1">2025 — Ekologiset Projektit <a class="header-anchor" href="#_2025-—-ekologiset-projektit" aria-label="Permalink to &quot;2025 — Ekologiset Projektit&quot;">​</a></h3><p>Kolmen innovatiivisen ratkaisun kehittäminen kestävää tulevaisuutta varten:</p><p><strong>UCTS</strong> — järjestelmä alumiiniteollisuuden myrkyllisen punalietteen muuntamiseksi energiaksi ja rakennusmateriaaleiksi. Insinöörin vastaus ekologiseen kriisiin.</p><p><strong>Solar-H2</strong> — hybridi merenkulun propulsiojärjestelmä, joka yhdistää vetypolttokennot ja aurinkoenergian. Innovatiivinen ratkaisu meriteollisuuden hiilidioksidipäästöjen vähentämiseksi.</p><p><strong>Moss Tile</strong> — luonnollinen kaupunkiviilennysjärjestelmä elävää sammalta käyttäen. Teknologian integrointi luontoon tasapainoista ja suvereenia tulevaisuutta varten.</p></div></div>',2)])])}const v=a(n,[["render",o]]);export{d as __pageData,v as default};
