import{_ as t,o,c as r,ae as e}from"./chunks/framework.9XX48Qcy.js";const p=JSON.parse('{"title":"Детальна Хронологія Подій (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Детальна Хронологія Подій (1993-2025)"},"headers":[],"relativePath":"history.md","filePath":"history.md"}'),i={name:"history.md"};function s(l,a,d,n,h,c){return o(),r("div",null,[...a[0]||(a[0]=[e('<h1 id="детальна-хронологія-подіи-1993-2025" tabindex="-1">Детальна Хронологія Подій (1993-2025) <a class="header-anchor" href="#детальна-хронологія-подіи-1993-2025" aria-label="Permalink to &quot;Детальна Хронологія Подій (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"><h3 id="_1993-2001-—-пастка-громадянства" tabindex="-1">1993-2001 — Пастка громадянства <a class="header-anchor" href="#_1993-2001-—-пастка-громадянства" aria-label="Permalink to &quot;1993-2001 — Пастка громадянства&quot;">​</a></h3><p>Я народився на Донбасі — землі праці та великих викликів. Моє дитинство було позначене юридичною аномалією. Отримавши два паспорти (України та РФ) через розпад СРСР, я став заручником системи, яка використала цю колізію як зброю проти мого суверенітету.</p></div><div class="history-block"><h3 id="_2014-—-віина-на-донбасі" tabindex="-1">2014 — Війна на Донбасі <a class="header-anchor" href="#_2014-—-віина-на-донбасі" aria-label="Permalink to &quot;2014 — Війна на Донбасі&quot;">​</a></h3><p>Війна забрала у мене дім, родину та відчуття стабільності. Після 2014 року моє майно було незаконно відчужене. Це був початок мого шляху як людини, яка мусить захищати своє право на існування.</p></div><div class="history-block"><h3 id="_2020-2022-—-правозахисна-діяльність" tabindex="-1">2020-2022 — Правозахисна діяльність <a class="header-anchor" href="#_2020-2022-—-правозахисна-діяльність" aria-label="Permalink to &quot;2020-2022 — Правозахисна діяльність&quot;">​</a></h3><p>Активна участь у русі за легалізацію медичного канабісу в Україні. Співпраця з блогерами «Растамани України» — спільнотою, яка бореться за право людей на альтернативне лікування. Ми виступали за зменшення страждань онкохворих та людей з хронічним болем, які не мають доступу до ефективного знеболення. Це був час активної громадянської позиції та публічних виступів.</p></div><div class="history-block"><h3 id="_2024-—-системнии-гніт" tabindex="-1">2024 — Системний гніт <a class="header-anchor" href="#_2024-—-системнии-гніт" aria-label="Permalink to &quot;2024 — Системний гніт&quot;">​</a></h3><p>Сьогодні я борюся проти бюрократичного гніту в Німеччині. Позбавлення прав на медичну допомогу та пересування — це сучасна форма тортур, спрямована на зламання волі. Але я — Жива Людина, і я продовжую творити.</p></div><div class="history-block"><h3 id="_2025-—-екологічні-проєкти" tabindex="-1">2025 — Екологічні проєкти <a class="header-anchor" href="#_2025-—-екологічні-проєкти" aria-label="Permalink to &quot;2025 — Екологічні проєкти&quot;">​</a></h3><p>Розробка трьох інноваційних рішень для сталого майбутнього:</p><p><strong>UCTS (Universal Carbon Transformation System)</strong> — система перетворення токсичного червоного шламу алюмінієвої промисловості на енергію та будівельні матеріали. Інженерна відповідь на екологічну кризу.</p><p><strong>Solar-H2</strong> — гібридна система морського руху, що поєднує водневі паливні елементи та сонячну енергію. Інноваційне рішення для декарбонізації морської галузі.</p><p><strong>Moss Tile</strong> — система природного охолодження міст за допомогою живого моху. Інтеграція технологій у природу для збалансованого та суверенного майбутнього.</p></div></div>',2)])])}const b=t(i,[["render",s]]);export{p as __pageData,b as default};
