import{_ as r,o as i,c as t,ae as n}from"./chunks/framework.9XX48Qcy.js";const d=JSON.parse('{"title":"Ítarleg Tímaröð (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Ítarleg Tímaröð (1993-2025)"},"headers":[],"relativePath":"is/history.md","filePath":"i18n/is/history.md"}'),s={name:"is/history.md"};function e(l,a,o,g,f,k){return i(),t("div",null,[...a[0]||(a[0]=[n('<h1 id="itarleg-timaroð-1993-2025" tabindex="-1">Ítarleg Tímaröð (1993-2025) <a class="header-anchor" href="#itarleg-timaroð-1993-2025" aria-label="Permalink to &quot;Ítarleg Tímaröð (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"> ### 1993-2001 — Ríkisfangsagildran Fæddur í Donbass — landi vinnu og mikilla áskorana. Æska mín markaðist af lagalegu fráviki. Með tvö vegabréf (Úkraínu og Rússland) vegna hruns Sovétríkjanna, varð ég gísl kerfis sem notaði þennan árekstur sem vopn gegn fullveldi mínu. </div><div class="history-block"> ### 2014 — Stríð í Donbass Stríðið tók heimili mitt, fjölskyldu og tilfinningu fyrir stöðugleika. Eftir 2014 var eign mín tekin ólöglega. Þetta var upphafið að vegferð minni sem maður sem verður að verja rétt sinn til að vera til. </div><div class="history-block"> ### 2020-2022 — Mannréttindabarátta Virk þátttaka í hreyfingunni fyrir lögleiðingu læknisfræðilegs kannabis í Úkraínu ásamt &#39;Rastamany Ukraine&#39; bloggurum. Barátta fyrir rétti fólks til óhefðbundinna meðferða og minnkunar þjáninga. </div><div class="history-block"> ### 2024 — Kerfisbundin Kúgun Í dag berst ég gegn skrifræðiskúgun í Þýskalandi. Svipting réttar til læknishjálpar og ferðafrelsis er nútímaform pyntinga sem miðar að því að brjóta viljann. En ég er Lifandi Maður, og ég held áfram að skapa. </div><div class="history-block"> ### 2025 — Vistfræðileg Verkefni Þróun þriggja nýstárlegra lausna fyrir sjálfbæra framtíð: <p><strong>UCTS</strong> — kerfi til að breyta eitraðri rauðri leðju frá áliðnaði í orku og byggingarefni. Verkfræðilegt svar við vistfræðilegu kreppunni.</p><p><strong>Solar-H2</strong> — tvíorku sjávarknúningarkerfi sem sameinar vetniseldsneytissellur og sólarorku. Nýstárleg lausn til að kolefnishreinsa sjávariðnaðinn.</p><p><strong>Moss Tile</strong> — náttúrulegt borgarkælikerfi með lifandi mosa. Samþætting tækni við náttúru fyrir jafnvægi og fullvalda framtíð.</p></div></div>',2)])])}const m=r(s,[["render",e]]);export{d as __pageData,m as default};
