import{_ as n,o as i,c as o,ae as t}from"./chunks/framework.9XX48Qcy.js";const v=JSON.parse('{"title":"Gedetailleerde Chronologie (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Gedetailleerde Chronologie (1993-2025)"},"headers":[],"relativePath":"nl/history.md","filePath":"i18n/nl/history.md"}'),a={name:"nl/history.md"};function r(s,e,d,l,g,c){return i(),o("div",null,[...e[0]||(e[0]=[t('<h1 id="gedetailleerde-chronologie-1993-2025" tabindex="-1">Gedetailleerde Chronologie (1993-2025) <a class="header-anchor" href="#gedetailleerde-chronologie-1993-2025" aria-label="Permalink to &quot;Gedetailleerde Chronologie (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"> ### 1993-2001 — Staatsburgerschapsval Geboren in Donbass — een land van arbeid en grote uitdagingen. Mijn kindertijd werd gekenmerkt door een juridische anomalie. Met twee paspoorten (Oekraïne en Rusland) door de ineenstorting van de USSR, werd ik gegijzeld door een systeem dat deze botsing als wapen tegen mijn soevereiniteit gebruikte. </div><div class="history-block"> ### 2014 — Oorlog in Donbass De oorlog nam mijn huis, mijn familie en mijn gevoel van stabiliteit. Na 2014 werd mijn eigendom illegaal in beslag genomen. Dit was het begin van mijn pad als mens die zijn recht om te bestaan moet verdedigen. </div><div class="history-block"> ### 2020-2022 — Mensenrechtenactivisme Actieve deelname aan de beweging voor legalisatie van medicinale cannabis in Oekraïne samen met &#39;Rastamany Ukraine&#39; bloggers. Vechten voor het recht van mensen op alternatieve behandeling en vermindering van lijden. </div><div class="history-block"> ### 2024 — Systemische Onderdrukking Vandaag vecht ik tegen bureaucratische onderdrukking in Duitsland. Ontneming van rechten op medische zorg en beweging is een moderne vorm van marteling gericht op het breken van de wil. Maar ik ben een Levend Mens, en ik blijf scheppen. </div><div class="history-block"> ### 2025 — Ecologische Projecten Ontwikkeling van drie innovatieve oplossingen voor een duurzame toekomst: <p><strong>UCTS</strong> — systeem voor het omzetten van giftig rood slib uit de aluminiumindustrie in energie en bouwmaterialen. Een ingenieursantwoord op de ecologische crisis.</p><p><strong>Solar-H2</strong> — een hybride maritiem voortstuwingssysteem dat waterstofbrandstofcellen en zonne-energie combineert. Een innovatieve oplossing voor het koolstofvrij maken van de maritieme industrie.</p><p><strong>Moss Tile</strong> — een natuurlijk stadskoelingssysteem met levend mos. Technologie integreren in de natuur voor een evenwichtige en soevereine toekomst.</p></div></div>',2)])])}const h=n(a,[["render",r]]);export{v as __pageData,h as default};
