import{_ as n,o as i,c as a,ae as r}from"./chunks/framework.9XX48Qcy.js";const v=JSON.parse('{"title":"VERKLARING VAN AANWEZIGHEID VAN EEN LEVEND MENS","description":"","frontmatter":{"layout":"doc","title":"VERKLARING VAN AANWEZIGHEID VAN EEN LEVEND MENS"},"headers":[],"relativePath":"nl/letters/mfa_iceland.md","filePath":"i18n/nl/letters/mfa_iceland.md"}'),t={name:"nl/letters/mfa_iceland.md"};function s(o,e,d,l,g,c){return i(),a("div",null,[...e[0]||(e[0]=[r('<h1 id="verklaring-van-aanwezigheid-van-een-levend-mens" tabindex="-1">VERKLARING VAN AANWEZIGHEID VAN EEN LEVEND MENS <a class="header-anchor" href="#verklaring-van-aanwezigheid-van-een-levend-mens" aria-label="Permalink to &quot;VERKLARING VAN AANWEZIGHEID VAN EEN LEVEND MENS&quot;">‚Äã</a></h1><div class="letter-print-container vp-doc"><div class="no-print" style="margin-bottom:2rem;"><a href="javascript:window.print()" class="link">Document Afdrukken üñ®Ô∏è</a></div><p><b>Aan het Ministerie van Buitenlandse Zaken van IJsland en internationale mensenrechtenorganisaties.</b></p><div class="letter-body-content"><p>Ik, Aleksandr Snigirev, verklaar door deze openbare verklaring de aanwezigheid van een Levend Mens, begiftigd met geest, geweten en onvervreemdbare rechten verkregen door geboorterecht op Aarde.</p><p>Deze verklaring is gebaseerd op de <strong>Universele Verklaring van de Rechten van de Mens van de VN</strong> (Artikelen 1, 3, 6, 13, 14, 15) en hoger Natuurrecht.</p><h3 id="_1-juridische-status" tabindex="-1">1. Juridische Status <a class="header-anchor" href="#_1-juridische-status" aria-label="Permalink to &quot;1. Juridische Status&quot;">‚Äã</a></h3><p>Ik verklaar dat ik geen &#39;natuurlijk persoon&#39;, &#39;economisch middel&#39; of enige andere juridische fictie ben die door de staat is gecre√´erd zonder mijn bewuste toestemming. Ik handel als een vrij man, soeverein in mijn leven. Mijn aanwezigheid op deze Aarde is zelfvoorzienend, en mijn rechten op leven, vrijheid en creativiteit zijn absoluut en onvervreemdbaar.</p><h3 id="_2-kern-van-de-claim-en-mensenrechtenschendingen" tabindex="-1">2. Kern van de Claim en Mensenrechtenschendingen <a class="header-anchor" href="#_2-kern-van-de-claim-en-mensenrechtenschendingen" aria-label="Permalink to &quot;2. Kern van de Claim en Mensenrechtenschendingen&quot;">‚Äã</a></h3><p>In het afgelopen jaar heb ik onder ongekende en systemische bureaucratische druk gestaan in de Bondsrepubliek Duitsland. Deze druk manifesteert zich als volgt:</p><ul><li><strong>Ontneming van Bewegingsvrijheid</strong>: Mijn recht op vrije keuze van woonplaats en beweging is ongegrond beperkt.</li><li><strong>Beperking van Recht op Leven en Gezondheid</strong>: Mij is de toegang tot noodzakelijke medische hulp ontzegd, wat een directe bedreiging voor mijn gezondheid vormt.</li><li><strong>Psychologische Druk</strong>: Het systeem cre√´ert &#39;digitale getto&#39;-omstandigheden in een poging mij te dwingen afstand te doen van mijn overtuigingen en soevereine status.</li></ul><p>Deze acties zijn directe schendingen van fundamentele mensenrechten en kunnen worden gekwalificeerd als marteling en onmenselijke behandeling.</p><h3 id="_3-verzoek-om-bescherming-en-erkenning" tabindex="-1">3. Verzoek om Bescherming en Erkenning <a class="header-anchor" href="#_3-verzoek-om-bescherming-en-erkenning" aria-label="Permalink to &quot;3. Verzoek om Bescherming en Erkenning&quot;">‚Äã</a></h3><p>Ik doe officieel een beroep op de Regering en het Volk van de Republiek IJsland, als een land met diepe tradities van respect voor mensenrechten en individuele soevereiniteit:</p><ul><li><strong>Ik verzoek om politiek asiel</strong> en internationale bescherming tegen systemische vervolging.</li><li><strong>Ik verzoek om erkenning van mijn status als Levend Mens</strong> en de mogelijkheid om mijn ingenieurs- en creatieve projecten te realiseren ten behoeve van de mensheid.</li></ul></div><div class="letter-signature"><p>Ondertekeningsdatum: 5 februari 2024</p><p><b>Handtekening:</b> <span class="nowrap">Aleksandr Snigirev</span>, <br><span class="nowrap"><b>Levend Mens</b></span></p></div></div>',2)])])}const m=n(t,[["render",s]]);export{v as __pageData,m as default};
