import{_ as t,o as r,c as s,ae as i}from"./chunks/framework.9XX48Qcy.js";const h=JSON.parse('{"title":"Detaljert Kronologi (1993-2025)","description":"","frontmatter":{"layout":"doc","title":"Detaljert Kronologi (1993-2025)"},"headers":[],"relativePath":"no/history.md","filePath":"i18n/no/history.md"}'),a={name:"no/history.md"};function o(n,e,l,k,d,g){return r(),s("div",null,[...e[0]||(e[0]=[i('<h1 id="detaljert-kronologi-1993-2025" tabindex="-1">Detaljert Kronologi (1993-2025) <a class="header-anchor" href="#detaljert-kronologi-1993-2025" aria-label="Permalink to &quot;Detaljert Kronologi (1993-2025)&quot;">​</a></h1><div class="history-detail vp-doc"><div class="history-block"><h3 id="_1993-2001-—-statsborgerskapsfellen" tabindex="-1">1993-2001 — Statsborgerskapsfellen <a class="header-anchor" href="#_1993-2001-—-statsborgerskapsfellen" aria-label="Permalink to &quot;1993-2001 — Statsborgerskapsfellen&quot;">​</a></h3><p>Født i Donbass — et land med arbeid og store utfordringer. Barndommen min var preget av en juridisk anomali. Med to pass (Ukraina og Russland) på grunn av Sovjetunionens kollaps, ble jeg gissel for et system som brukte denne kollisjonen som et våben mot min suverenitet.</p></div><div class="history-block"><h3 id="_2014-—-krig-i-donbass" tabindex="-1">2014 — Krig i Donbass <a class="header-anchor" href="#_2014-—-krig-i-donbass" aria-label="Permalink to &quot;2014 — Krig i Donbass&quot;">​</a></h3><p>Krigen tok mitt hjem, min familie og min følelse av stabilitet. Etter 2014 ble min eiendom ulovlig beslaglagt. Dette var begynnelsen på min vei som et menneske som må forsvare sin rett til å eksistere.</p></div><div class="history-block"><h3 id="_2020-2022-—-menneskerettighetsaktivisme" tabindex="-1">2020-2022 — Menneskerettighetsaktivisme <a class="header-anchor" href="#_2020-2022-—-menneskerettighetsaktivisme" aria-label="Permalink to &quot;2020-2022 — Menneskerettighetsaktivisme&quot;">​</a></h3><p>Aktiv deltakelse i bevegelsen for legalisering av medisinsk cannabis i Ukraina sammen med &#39;Rastamany Ukraine&#39; bloggere. Kjempet for folks rett til alternativ behandling og reduksjon av lidelser.</p></div><div class="history-block"><h3 id="_2024-—-systemisk-undertrykkelse" tabindex="-1">2024 — Systemisk Undertrykkelse <a class="header-anchor" href="#_2024-—-systemisk-undertrykkelse" aria-label="Permalink to &quot;2024 — Systemisk Undertrykkelse&quot;">​</a></h3><p>I dag kjemper jeg mot byråkratisk undertrykkelse i Tyskland. Fratakelse av rettigheter til legehjelp og bevegelse er en moderne form for tortur rettet mot å knekke viljen. Men jeg er et Levende Menneske, og jeg fortsetter å skape.</p></div><div class="history-block"><h3 id="_2025-—-økologiske-prosjekter" tabindex="-1">2025 — Økologiske Prosjekter <a class="header-anchor" href="#_2025-—-økologiske-prosjekter" aria-label="Permalink to &quot;2025 — Økologiske Prosjekter&quot;">​</a></h3><p>Utvikling av tre innovative løsninger for en bærekraftig fremtid:</p><p><strong>UCTS</strong> — system for å omdanne giftig rødslam fra aluminiumsindustrien til energi og byggematerialer. Et ingeniørsvar på den økologiske krisen.</p><p><strong>Solar-H2</strong> — et hybrid maritimt fremdriftssystem som kombinerer hydrogenbrenselceller og solenergi. En innovativ løsning for avkarbonisering av den maritime industrien.</p><p><strong>Moss Tile</strong> — et naturlig bykjølingssystem ved bruk av levende mose. Integrerer teknologi i naturen for en balansert og suveren fremtid.</p></div></div>',2)])])}const v=t(a,[["render",o]]);export{h as __pageData,v as default};
