import{_ as t,o as n,c as s,ae as r}from"./chunks/framework.9XX48Qcy.js";const p=JSON.parse('{"title":"ERKL√ÜRING OM TILSTEDEV√ÜRELSE AV ET LEVENDE MENNESKE","description":"","frontmatter":{"layout":"doc","title":"ERKL√ÜRING OM TILSTEDEV√ÜRELSE AV ET LEVENDE MENNESKE"},"headers":[],"relativePath":"no/letters/mfa_iceland.md","filePath":"i18n/no/letters/mfa_iceland.md"}'),i={name:"no/letters/mfa_iceland.md"};function a(l,e,o,d,g,k){return n(),s("div",null,[...e[0]||(e[0]=[r('<h1 id="erkl√¶ring-om-tilstedev√¶relse-av-et-levende-menneske" tabindex="-1">ERKL√ÜRING OM TILSTEDEV√ÜRELSE AV ET LEVENDE MENNESKE <a class="header-anchor" href="#erkl√¶ring-om-tilstedev√¶relse-av-et-levende-menneske" aria-label="Permalink to &quot;ERKL√ÜRING OM TILSTEDEV√ÜRELSE AV ET LEVENDE MENNESKE&quot;">‚Äã</a></h1><div class="letter-print-container vp-doc"><div class="no-print" style="text-align:right;margin-bottom:1rem;"><a href="javascript:window.print()" class="link-btn">Skriv ut Dokument üñ®Ô∏è</a></div><p><b>Til det Islandske Utenriksdepartementet og internasjonale menneskerettighetsorganisasjoner.</b></p><div class="letter-body-content"><p>Jeg, Aleksandr Snigirev, erkl√¶rer ved denne offentlige erkl√¶ringen tilstedev√¶relsen av et Levende Menneske, utstyrt med √•nd, samvittighet og umistelige rettigheter mottatt ved f√∏dselsrett p√• Jorden.</p><p>Denne erkl√¶ringen er basert p√• <strong>FNs Verdenserkl√¶ring om Menneskerettigheter</strong> (Artikler 1, 3, 6, 13, 14, 15) og h√∏yere Naturrett.</p><h3 id="_1-juridisk-status" tabindex="-1">1. Juridisk Status <a class="header-anchor" href="#_1-juridisk-status" aria-label="Permalink to &quot;1. Juridisk Status&quot;">‚Äã</a></h3><p>Jeg erkl√¶rer at jeg ikke er en &#39;fysisk person&#39;, &#39;√∏konomisk ressurs&#39; eller noen annen juridisk fiksjon skapt av staten uten mitt bevisste samtykke. Jeg handler som en fri mann, suveren i mitt liv. Min tilstedev√¶relse p√• denne Jorden er selvforsynt, og mine rettigheter til liv, frihet og kreativitet er absolutte og umistelige.</p><h3 id="_2-kravets-essens-og-brudd-pa-menneskerettigheter" tabindex="-1">2. Kravets Essens og Brudd p√• Menneskerettigheter <a class="header-anchor" href="#_2-kravets-essens-og-brudd-pa-menneskerettigheter" aria-label="Permalink to &quot;2. Kravets Essens og Brudd p√• Menneskerettigheter&quot;">‚Äã</a></h3><p>I det siste √•ret har jeg v√¶rt under enest√•ende og systemisk byr√•kratisk press i Forbundsrepublikken Tyskland. Dette presset manifesterer seg som f√∏lger:</p><ul><li><strong>Fratakelse av Bevegelsesfrihet</strong>: Min rett til fritt valg av bosted og bevegelse er grunnl√∏st begrenset.</li><li><strong>Begrensning av Retten til Liv og Helse</strong>: Jeg har blitt nektet tilgang til n√∏dvendig medisinsk hjelp, noe som skaper en direkte trussel mot min helse.</li><li><strong>Psykologisk Press</strong>: Systemet skaper &#39;digitale ghetto&#39;-forhold i et fors√∏k p√• √• tvinge meg til √• gi avkall p√• mine overbevisninger og suverene status.</li></ul><p>Disse handlingene er direkte brudd p√• grunnleggende menneskerettigheter og kan kvalifiseres som tortur og umenneskelig behandling.</p><h3 id="_3-anmodning-om-beskyttelse-og-anerkjennelse" tabindex="-1">3. Anmodning om Beskyttelse og Anerkjennelse <a class="header-anchor" href="#_3-anmodning-om-beskyttelse-og-anerkjennelse" aria-label="Permalink to &quot;3. Anmodning om Beskyttelse og Anerkjennelse&quot;">‚Äã</a></h3><p>Jeg appellerer offisielt til Regjeringen og Folket i Republikken Island, som et land med dype tradisjoner for respekt for menneskerettigheter og individuell suverenitet:</p><ul><li><strong>Jeg ber om politisk asyl</strong> og internasjonal beskyttelse mot systemisk forf√∏lgelse.</li><li><strong>Jeg ber om anerkjennelse av min status som et Levende Menneske</strong> og muligheten til √• realisere mine ingeni√∏r- og kreative prosjekter til fordel for menneskeheten.</li></ul></div><div class="letter-signature"><p>Undertegnelsesdato: 5. februar 2024</p><p><b>Underskrift:</b> <span class="nowrap">Aleksandr Snigirev</span>, <br><span class="nowrap"><b>Levende Menneske</b></span></p></div></div>',2)])])}const v=t(i,[["render",a]]);export{p as __pageData,v as default};
